CREATE TABLE `kissgo_link` (  `link_id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `display` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否显示',  `sort` int(10) unsigned NOT NULL DEFAULT '9999' COMMENT '显示排序',  `name` varchar(128) NOT NULL COMMENT '显示名称',  `url` varchar(128) NOT NULL COMMENT 'url',  `title` varchar(128) DEFAULT NULL COMMENT '描述',  `type` varchar(32) DEFAULT NULL COMMENT '友情链接类型',  `icon` varchar(256) DEFAULT NULL COMMENT '图片地址',  `target` enum('_blank','_self') DEFAULT NULL,  `note` varchar(256) DEFAULT NULL,  PRIMARY KEY (`link_id`),  KEY `IDX_DISPLAY` (`display`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='友情链接';